dict.string:
#!/bin/bash
PATH=$PATH:/usr/local/bin

pct2abs() {
	local pct=$1
	local sv=$2	
	printf "%.0f" $(echo "scale =2; ($pct * $sv) / 100" | bc)
}

get_abs() {
    local arg=$1
    local sv=$2
    case $arg in
        ">") echo $(( $sv - aW )) ;;
        [vV]) echo $(( $sv - aH )) ;;
        "|") echo "h_center" ;;
        "-") echo "v_center" ;;
        *) if [[ $arg == *% ]]; then
                echo $(pct2abs ${arg%?} $sv)
           else
                echo $arg
           fi ;;
    esac
}

svL="$KMVAR_local_svLeft"
svT="$KMVAR_local_svTop"
svW="$KMVAR_local_svWidth"
svH="$KMVAR_local_svHeight"

IFS=' ' read -r x y w h extra <<< "$KMVAR_local_WindowArgs"

aW=$(get_abs $w $svW)
aH=$(get_abs $h $svH)

aX=$(get_abs $x $svW)
aY=$(get_abs $y $svH)

[[ $aX == "h_center" ]] && aX=$(( (svW - aW) / 2 ))
[[ $aY == "v_center" ]] && aY=$(( (svH - aH) / 2 ))

aX=$(( aX+svL ))
aY=$(( aY+svT ))

echo "$aX,$aY,$aW,$aH"
