#!/usr/bin/perl

my $dir = $ENV{KMVAR_favsHome};
my @files = ("apps.txt", "files.txt", "folders.txt"); 
my $listing = "";

foreach $file (@files) {
	my $path = "$dir$file";
	open my $info, $path or die "Fail: $path: $!";

	while( my $line = <$info>)  {
		if ($line =~ /^(\S.+?)$/) {	# heading
		;
		} elsif ($line =~ /^\t(.+?)\t(.+?)(\t.+?)?$/) {
			$listing .= "$2__$1$3\n";
		}
	}
}

$listing .= "[Edit Apps]\n[Edit Files]\n[Edit Folders]\n[Help]\n";

print $listing;

#!/usr/bin/perl

my $dir = $ENV{KMVAR_favsHome};
my $file = "apps.txt";
my $listing = "";
my $count = 0;
my $labels = 0;

my $path = "$dir$file";
open my $info, $path or die "Fail: $path: $!";

while( my $line = <$info>)  {
	if ($line =~ /^#/) {			# comment
		$count--;
	} elsif ($line =~ /^(\S.+?)$/) {	# heading
		if ($count > 0) {
			$listing .= "\t</optgroup>\n\t<option disabled></option>\n";
		}
		$listing .= "\t<optgroup label=\"$1\">\n";
		$labels++;
	} elsif ($line =~ /^\t(.+?)\t(.+?)(\t.+?)?$/) {
		$listing .= "\t\t<option value=\"$2\">$1</option>\n";
	}
	$count++;
}
if ($labels > 0) { $listing .= "\t</optgroup>\n"; }
close($info);

print $listing;

