(function() {
	'use strict';

	var app = Application.currentApplication();
	app.includeStandardAdditions = true;

	try {
		var result = app.chooseFolder({withPrompt: "Select the 'Macro Import Manager Resources' folder"}).toString();
		if (!result.endsWith("/")) result += "/";
		return result;
	} catch (e) {
		return "";
	}
})();
