SELECT COUNT(*)
FROM macros_new
WHERE Macro_Status IS NULL OR Macro_Status='';

CREATE TEMP TABLE temp_macro_list (Macro_ID EXT);

INSERT INTO temp_macro_list (Macro_ID) VALUES
%Variable%local_theModifiedSQL%;

UPDATE macros_new
SET Macro_Status = 'modified'
WHERE Macro_ID IN (SELECT Macro_ID FROM temp_macro_list);

DROP TABLE IF EXISTS temp_macro_list;

#!/usr/bin/perl

# Your data
my $data = $ENV{KMVAR_instance_ModToProcessForReport};

# Split the data into lines
my @lines = split(/\n/, $data);

# Initialize variables to store the current group and category
my $current_group = "";
my $current_category = "";

# Loop through the lines and process the data
foreach my $line (@lines) {
    # Extract the group name, category, and item name
    my ($group_name, $category, $item) = $line =~ /^(.*?)‚Üêüî∏‚Üí(.*?)‚Üêüî∏‚Üí(.*)$/;

    # Remove leading and trailing spaces from the item name
    #$item =~ s/^\s+|\s+$//g;

    # Check if the group has changed
    if ($group_name ne $current_group) {
        $current_group = $group_name;
        print "";
        print "\n ‚ñ™Ô∏è $current_group\n";
        $current_category = "";
    }

    # Check if the category has changed
    if ($category ne $current_category) {
        $current_category = $category;
        print "\n";
        print "       $current_category\n";
    }

    # Print the item name
    print "         $item\n";
}
