'use strict';	// See http://www.w3schools.com/js/js_strict.asp
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

(function myMain() {		// ~~~ automatically executed when this script is executed ~~~

var ptyScriptName 	= "Get Simple List of KM Macro Based on Filter";
var ptyScriptVer 		= "1.0";
var ptyScriptDate 	= "2021-03-13";
var ptyScriptAuthor = "JMichaelTX";		// based on JXA script by @DanThomas
/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
PURPOSE:  Demo How to Get List of Macros from KM Engine

RETURNS: Text List of Macro Names that meet filter (from KM Macro) conditions

	• IF an unresolved ERROR occurs, THEN:
		• Display Notification with error msg
		• Throw an error with actual error msg, which will cause the KM Script Action to fail and terminate the Macro. (requires KM 9+)

KM VARIABALES REQUIRED:
	• Local_FilterBy__MacroGroupName
	•	Local_FilterBy__MacroNameContains

KM VARIABLES SET:
	• NONE

REF:
	• https://forum.keyboardmaestro.com/t/macro-execute-trigger-macro-by-name-spotlight/4667
~~~~~~~~~~~~~~~~~~~ END HEADER COMMENTS ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
//--- GLOBAL VARIABLES ---
var gDebug = false;

// --- SET CURRENT APP VARIABLE NEEDED FOR DIALOGS & StandardAdditions.osax ---
var app = Application.currentApplication()
app.includeStandardAdditions = true

// --- KM ENGINE APP ---
var kmInst = app.systemAttribute("KMINSTANCE");	// needed for Local & Instance variables
var kmeApp = Application("Keyboard Maestro Engine")

var scriptResults = "OK"	// Set your results to this var


try {	//~~~~~~~~~~~ START TRY ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
	//	GET KM VARIABLES
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
	 
	var groupNameToFind = kmeApp.getvariable("Local_FilterBy__MacroGroupName",  {instance: kmInst}); // || '[GLOBAL]';
	var macroNameContains = kmeApp.getvariable("Local_FilterBy__MacroNameContains",  {instance: kmInst});	// || 'BBEdit';

	//### YOUR CODE HERE ###

	var macroList	= getAllKMMacros();		// returns an array of Macro objects

	if(gDebug) { app.setTheClipboardTo(JSON.stringify(macroList, undefined, 4)); }

	//--- Make Below Filter Comparisons Be Case Insensitive ---
	groupNameToFind = groupNameToFind.toLowerCase();
	macroNameContains	= macroNameContains.toLowerCase();

	var macroListFiltered = macroList.filter(function(elem) {
			if (elem['groupName'] && elem['macroName']) {
				var groupNameFound = (elem['groupName'].toLowerCase() === groupNameToFind);
				var macroNameFound	= (elem['macroName'].toLowerCase().indexOf(macroNameContains) !== -1);
				return (groupNameFound && macroNameFound);
			} else { return false; }
			});

	//--- Build Simple Array (List) of Just Macro Names ---
	var macroNameList	= macroListFiltered.map(e => {return e['macroName']});   // e is element value

	//--- Set Results to Text List ---
	scriptResults	= macroNameList.join('\n');

} //~~~~ END TRY ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

catch (oError) {

	if (oError.errorNumber === -128) {	// User Canceled
			scriptResults =  "[USER_CANCELED]\n\n"			+ "SCRIPT: " + ptyScriptName + "   Ver: " + ptyScriptVer
	}
		else {
		var errNum = oError.errorNumber || "Custom Error";		scriptResults = "[ERROR]\n\n"			+ "SCRIPT: " + ptyScriptName + "   Ver: " + ptyScriptVer + "\n"			+ "Error Number: " + errNum + "\n"			+ oError.message

			var errMsg = oError.message;

			//~~~~ Display Notification & Throw Error ~~~
			//	(will cause KM Script Action to fail, and terminate macro)

			app.displayNotification(
				errMsg, 
				{
					withTitle: ptyScriptName,
					subtitle:  '[ERROR] Macro CANCELLED',
					soundName: "Glass"
			  })

			throw new Error(errMsg);
				} // END if/else on ERROR Number

} //~~~~ END TRY/CATCH BLOCK ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

//--- RETURN ---
return scriptResults

//=============== END OF MAIN SCRIPT =======================================

//### YOUR FUNCTIONS HERE ###

function extractMacrosFromPlist(pPlist) {
	//--- Function Based Mostly on "execute()" function by @DanThomas ---
	//--- Build a simple array of Macro objects;  Compresses the multi-tier arrary of the plist ---

		var macroList = [];
		pPlist.forEach(function(group) {
			if (group.macros) {
				group.macros.forEach(function(macro) {
					if (macro.active && macro.enabled) {
						var triggers = "";
						if (macro.triggers && macro.triggers.length > 0) {
							var triggers = macro.triggers.map(function(trigger) {
								return trigger.short;
							}).join(" | \n");
						}
						if (triggers)
							triggers = "; Triggers: " + triggers;
						macroList.push({
							macroUUID: macro.uid,
							macroName: macro.name,
							groupName: group.name,
							triggers: triggers
						});
					}
				});
			}
    });
 	return macroList;
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
function getAllKMMacros() {
/*			Ver 1.0		2021-03-13
---------------------------------------------------------------------------------
	PURPOSE:  Get All KM Macros from KM Engine

	RETURNS:	Array of Objects, each Object is a Macro
	REF:  Based on JXA Script by @DanThomas
		1. 

—————————————————————————————————————————————————————————————————————————————————
*/
	var kmeApp = Application("Keyboard Maestro Engine");

	//--- Get All KM Macros from KM Engine in XML (plist) Format ---
	var macroListXML	= kmeApp.getmacros({asstring: true})

	var macroArray = ObjC.deepUnwrap(
					$.NSPropertyListSerialization.propertyListWithDataOptionsFormatError(
						$(macroListXML).dataUsingEncoding($.NSUTF8StringEncoding), 0, 0, null));

	var macroList = extractMacrosFromPlist(macroArray)
	return macroList;

} //~~~~~~~~~~~~~~~ END OF function getAllKMMacros ~~~~~~~~~~~~~~~~~~~~~~~~~


})();	// ~~~ function myMain() is automatically executed when this script is executed ~~~
