property ptyScriptName : "Open Last N KM Log Lines in BBEdit"property ptyScriptVer : "1.0"property ptyScriptDate : "2020-05-24"property ptyScriptAuthor : "JMichaelTX"set kmInst to system attribute "KMINSTANCE"tell application "Keyboard Maestro Engine" to set numLogLines to getvariable "Local__LogLinesToOpen" instance kmInstif (numLogLines = "") then set numLogLines to "50"set cmdStr to "tail -r -n " & numLogLines & " ~/\"Library/Logs/Keyboard Maestro/Engine.log\""set logListStr to do shell script cmdStrtell application "BBEdit"	activate	set oDoc to make new text window with properties {contents:logListStr}
	tell oDoc to select insertion point before its text
end tell
