(function(inDesignMode, designModeParams) {
	'use strict';
	ObjC.import('AppKit');

	if (!String.prototype.startsWith) {
		String.prototype.startsWith = function(searchString, position) {
			position = position || 0;
			return this.substr(position, searchString.length) === searchString;
		};
	}

	// #region ======== Global Variables =============================
	const _editorAppName = "Code";
    const _kmEngine = Application("Keyboard Maestro Engine");
    const _kmEditor = Application("Keyboard Maestro");
    const _currentApp = Application.currentApplication();
    _currentApp.includeStandardAdditions = true;
	// #endregion

    // #region ======== Utils ===================================
    function getKMVariable(name, required) {
		var result = undefined;
		if (inDesignMode && designModeParams)
			result = designModeParams[name];
		if (result === undefined) {
            if (name.match(/^Instance|^Local/)) {
                var inst = _currentApp.systemAttribute("KMINSTANCE");
                result = _kmEngine.getvariable(name, {instance: inst});
            } else {
                result = _kmEngine.getvariable(name);
            }
        }
        if (required && !result)
			throw new Error("Variable '" + name + "' is empty");
		return result;
	}

	function getBoolKMVariable(name) {
		return getKMVariable(name).search(/^(1|true)$/i) === 0;
	}

	function execute() {
        var macroUUIDs = getKMVariable("Local_MacroUUIDs", true).split("\n");
        var enable = getBoolKMVariable("Local Enable", true);
        macroUUIDs.forEach(macroUUID => { 
            if (macroUUID)
                _kmEditor.setmacroenable(macroUUID, {enable: enable});
        });
        return "OK";
	}

	// #region ======== Runner Code =============================
    if (inDesignMode) {
		try {
            return execute();
        } catch (e) {
            return "Error on line: " + e.line + ": " + e.message;
        } finally {
			try {
				if (_editorAppName && Application(_editorAppName).isRunning) {
					Application(_editorAppName).activate();
				}
			} catch (e) {
				console.log("Error in try..finally: " + e);
			}
		}
	} else {
        try {
            return execute();
        } catch (e) {
            return "Error: " + e.message;
        }
	}
	// #endregion
})(false);
