	var _allMacrosPlist = null;
	function getAllMacrosPlist() {
		if (_allMacrosPlist == null)
			_allMacrosPlist = PlistUtils.deserializeKMAllMacrosSourceFile();
		return _allMacrosPlist;
	}

	var _globalMacroGroup = undefined;
	function getGlobalMacroGroup() {
		if (_globalMacroGroup !== undefined)
			return _globalMacroGroup;

		var plist = getAllMacrosPlist();
		for (let i = 0; i < plist.MacroGroups.length; i++) {
			const group = plist.MacroGroups[i];
			if (group.Name === "Global Macro Group") {
				_globalMacroGroup = group;
				return group;
			}
		}
		_globalMacroGroup = null;
		return null;
	}

	function createEmptyGlobalMacroGroup() {
		var emptyMacrosString =
			'<?xml version="1.0" encoding="UTF-8"?> \n' +
			'<!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd"> \n' +
			'<plist version="1.0"> \n' +
			'<array> \n' +
			'	<dict> \n' +
			'		<key>Activate</key> \n' +
			'		<string>Normal</string> \n' +
			'		<key>CreationDate</key> \n' +
			'		<real>476327082.54374099</real> \n' +
			'		<key>Macros</key> \n' +
			'		<array> \n' +
			'		</array> \n' +
			'		<key>Name</key> \n' +
			'		<string>Global Macro Group</string> \n' +
			'		<key>ToggleMacroUID</key> \n' +
			'		<string>F27B683F-D94D-4A80-A804-BB520C5BD73A</string> \n' +
			'		<key>UID</key> \n' +
			'		<string>DA8512F7-F323-46B1-8319-752E0495CFD0</string> \n' +
			'	</dict> \n' +
			'</array> \n' +
			'</plist>';

		var globalMacroGroup = getGlobalMacroGroup();
		if (globalMacroGroup)
			emptyMacrosString = emptyMacrosString.replace("DA8512F7-F323-46B1-8319-752E0495CFD0", globalMacroGroup.UID);

		var plist = PlistUtils.deserializeFromXmlString(emptyMacrosString);
		return plist[0];
	}

	function getQuickMacros() {
		var path = KMEngine.getVariable("Local_QuickMacrosFilePath");
		if (!path)
			path = KMEngine.getAppSupportFolderName() + "Quick Macros.plist";
		if (!FileUtils.fileExists(path))
			throw Error("QuickMacro file doesn't exist: '" + path + "'");

		var plist = PlistUtils.deserializeFromXmlFile(path);
		if (Object.keys(plist).length === 0)
			throw new Error("There are apparently no Quick Macros found in Quick Macros.plist");
		return plist;
	}

	function getSourceQuickMacro(quickMacros) {
		var uuids = Object.keys(quickMacros);
		if (uuids.length === 1)
			return quickMacros[uuids[0]];

		var uuid = KMEngine.getVariable("Local_UUID");
		if (uuid) {
			let result = quickMacros[uuid];
			if (result)
				return result;
			let error = new Error(`Could not find a Quick Macro for UUID "${uuid}"`);
			error.myErrorCode = "not found";
			throw error;
		}

		var allMacrosPlist = getAllMacrosPlist();
		var filteredUUIDs = uuids.filter(uuid => PlistUtils.findMacro(allMacrosPlist, uuid) == null);
		if (filteredUUIDs.length === 1)
			return quickMacros[filteredUUIDs[0]];

		let error = new Error(`Multiple Quick Macros exist. You'll need to choose one.`);
		error.myErrorCode = "multiple";
		throw error;
	}

	function makeUniqueMacroName(macroName) {
		var globalMacroGroup = getGlobalMacroGroup();
		if (!globalMacroGroup)
			return macroName;

		var highestDupNumber = -1;
		for (let i = 0; i < globalMacroGroup.Macros.length; i++) {
			const macro = globalMacroGroup.Macros[i];
			var matches = macro.Name.match(/^Quick Macro - Imported( \((\d+)\)$|$)/);
			if (matches) {
				if (matches && matches.length !== 3)
					throw new Error(`Unexpected regex match array ${matches}`)
				var dupNumber = matches[2] || 0;
				highestDupNumber = Math.max(highestDupNumber, dupNumber);
			}
		}

		if (highestDupNumber < 0)
			return macroName;
		return `${macroName} (${highestDupNumber + 1})`;
	}

	function importQuickMacro(quickMacros) {
		var quickMacro = getSourceQuickMacro(quickMacros);
		if (!quickMacro)
			throw new Error(`Could not determine which Recorded Actions Set to import`);

		var macroName = makeUniqueMacroName("Quick Macro - Imported");
		var macro = PlistUtils.createEmptyMacro(macroName);
		macro.UID = KMEngine.getRandomUUID();
		macro.Actions = quickMacro;
		macro.Triggers = [];
		if (macro.Actions.length > 0) {
			if(macro.Actions[0].MacroActionType === "Semaphore")
				macro.Actions.splice(0, 1);
		}

		var group = createEmptyGlobalMacroGroup();
		group.Macros.push(macro);
		var str = PlistUtils.serializeToXmlString([group]);

		if (!inDesignMode || KMEngine.getVariable("debug_DontImport") !== "1")
			KMEditor.importMacros(str);
		return "OK";
	}

	function getUUIDs(quickMacros) {
		var uuids = Object.keys(quickMacros).sort();
		var currUUID = KMEngine.getVariable("DND__ImportQuickMacroUUID");

		var result = [];
		for (let i = 0; i < uuids.length; i++) {
			const uuid = uuids[i];
			result.push(`${uuid}__${uuid === currUUID ? "âœ“" : ""}Quick Macro${i + 1}`);
		}

		return result.join("\n");
	}

	function execute() {
		if (inDesignMode)
			KMEditor.getEditorApp().activate();
		var quickMacros = getQuickMacros();

		var action = KMEngine.getVariable("Local_Action", true);
		switch (action) {
			case "getUUIDs":
				return getUUIDs(quickMacros);
			case "import":
				return importQuickMacro(quickMacros);
			default:
				throw new Error(`Invalid Local_Action: "${action}"`);
		}
	}

	// #region ======== Runner Code =============================

	function getErrorResult(e) {
		if (e.myErrorCode) {
			let result = {
				ErrorCode: e.myErrorCode,
				ErrorMessage: e.message
			};
			return JSON.stringify(result, null, 2);
		}

		if (inDesignMode)
			return `Error on line: ${e.line}: ${e.message}`;
		return `Error: ${e.message}`;
	}

	if (inDesignMode) {
		try {
			return execute();
		} catch (e) {
			return getErrorResult(e);
		} finally {
			try {
				if (_editorAppName && Application(_editorAppName).isRunning) {
					Application(_editorAppName).activate();
				}
			} catch (e) {
				console.log("Error in try..finally: " + e);
			}
		}
	} else {
		try {
			return execute();
		} catch (e) {
			return getErrorResult(e);
		}
	}
