(function (inDesignMode, designModeParams) {
	'use strict';


	// #region ======== Global Variables =============================
	const _editorAppName = "Code";
	const _kme = Application("Keyboard Maestro Engine");
	const _currentApp = Application.currentApplication();
	_currentApp.includeStandardAdditions = true;
	// #endregion

	// #region ======== Helper Functions =============================
	function getKMVariable(name, required) {
		var result = undefined;
		if (inDesignMode && designModeParams)
			result = designModeParams[name];
		if (result === undefined) {
			if (name.match(/^Instance|^Local/)) {
				var inst = _currentApp.systemAttribute("KMINSTANCE");
				result = _kme.getvariable(name, { instance: inst });
			} else {
				result = _kme.getvariable(name);
			}
		}
		if (required && !result)
			throw new Error("Variable '" + name + "' is empty");
		return result;
	}
	// #endregion

	function getDictionaryNames() {
		var result = [];
		var dictionaries = _kme.dictionaries();
		for (let i = 0; i < dictionaries.length; i++) {
			result.push(dictionaries[i].name());
		}
		return result;
	}

	function execute() {
		var parameters = JSON.parse(getKMVariable("Local_parameters", true));
		var data = {};
		if (parameters.getAllGlobalVariableNames)
			data.allGlobalVariableNames = _kme.variables.name().join(",");
		if (parameters.getAllDictionaryNames)
			data.allDictionaryNames = getDictionaryNames().join(",");

		const script =
`<dict>
	<key>ActionUID</key>
	<integer>199489</integer>
	<key>DisplayKind</key>
	<string>None</string>
	<key>MacroActionType</key>
	<string>ExecuteJavaScriptForCustomPrompt</string>
	<key>Path</key>
	<string></string>
	<key>Text</key>
	<string>reloadData(\`${JSON.stringify(data)}\`);</string>
	<key>TimeOutAbortsMacro</key>
	<true/>
	<key>UseText</key>
	<true/>
	<key>WindowID</key>
	<string>${parameters.customHTMLPromptID}</string>
</dict>`;
		_kme.doScript(script);
	}

	// #region ======== Runner Code =============================
	if (inDesignMode) {
		try {
			return execute();
		} catch (e) {
			return "Error on line: " + e.line + ": " + e.message;
		} finally {
			try {
				if (_editorAppName && Application(_editorAppName).isRunning) {
					Application(_editorAppName).activate();
				}
			} catch (e) {
				console.log("Error in try..finally: " + e);
			}
		}
	} else {
		try {
			return execute();
		} catch (e) {
			return "Error: " + e.message;
		}
	}
	// #endregion

})(false);
