#!/usr/bin/perl

my $output_file = "/tmp/MBU_rgMacroCombined";

open my $var1_fh, '<', "/tmp/MBU_rgmacroGroups" or die "Could not open var1.txt: $!";
open my $var2_fh, '<', "/tmp/MBU_rgmacroUUIDs" or die "Could not open var2.txt: $!";
open my $var3_fh, '<', "/tmp/MBU_rgmacroNames" or die "Could not open var3.txt: $!";
open my $output_fh, '>', $output_file or die "Could not open $output_file: $!";

while (1) {
    my $line1 = <$var1_fh>;
    my $line2 = <$var2_fh>;
    my $line3 = <$var3_fh>;

    last unless defined $line1 && defined $line2 && defined $line3;

    chomp($line1);
    chomp($line2);
    chomp($line3);

    print $output_fh "$line1###$line2###$line3\n";
}

close $var1_fh;
close $var2_fh;
close $var3_fh;
close $output_fh;

