#!/usr/bin/perl

# Your data
my $data = $ENV{KMVAR_instance_ModToProcessForReport};

# Split the data into lines
my @lines = split(/\n/, $data);

# Initialize variables to store the current group and category
my $current_group = "";
my $current_category = "";

# Loop through the lines and process the data
foreach my $line (@lines) {
    # Extract the group name, category, and item name
    my ($group_name, $category, $item) = $line =~ /^(.*?)###(.*?)###(.*)$/;

    # Remove leading and trailing spaces from the item name
    #$item =~ s/^\s+|\s+$//g;

    # Check if the group has changed
    if ($group_name ne $current_group) {
        $current_group = $group_name;
        print "";
        print "\n ▪️ $current_group\n";
        $current_category = "";
    }

    # Check if the category has changed
    if ($category ne $current_category) {
        $current_category = $category;
        print "\n";
        print "       $current_category\n";
    }

    # Print the item name
    print "         $item\n";
}
