dict.string:
'use strict';
(function run() {

var ptyScriptName   = "Get Execute macro lines from KM Error Log"
var ptyScriptVer    = "1.0"  // CHR Error Handler to Throw Error
var ptyScriptDate   = "2020-05-25"
var ptyScriptAuthor = "JMichaelTX"
/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
RETURNS:  One of these, as text:
  • First 5 KM Log Entries that contain "Execute macro"
  • "[ERROR]" at start of results if a script error occurred.

KM VARIABALES REQUIRED:
  • Local__LastLogLines -- Last N Lines from KM Engine Log

TAGS:  @errors @KM @Macro @Log @JXA @ShellScript @ccstone
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
var scriptResults = "TBD"  // Set your results to this var

try {
  //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

var app = Application.currentApplication()
app.includeStandardAdditions = true

	// --- SET KME APP VARIABLE NEEDED TO GET/SET KM VARIABLES ---
	//			(remove if not needed)
	var kmeApp = Application("Keyboard Maestro Engine");
	var kmInst = app.systemAttribute("KMINSTANCE");

/*
//--- GET THE LAST LOG ENTRIES ---
var numLogsToGet = 50;
var cmdStr = "tail -r -n " + numLogsToGet +" ~/\"Library/Logs/Keyboard Maestro/Engine.log\""; // thanks to Chris Stone
var logListStr = app.doShellScript(cmdStr);
*/

var logListStr 		= kmeApp.getvariable("Local__LastLogLines",  {instance: kmInst});

//--- NOTE: KM Log entires appear to be separated by a CR ---
var logList = logListStr.split(/(?:\r?\n|\r)/);

//--- Filter Array to Contain ONLY Rows that Contain "Execute macro" ---
var logEM 	= logList.filter(pRow => /^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2} Execute macro/.test(pRow));
//console.log("logEM length: " + logEM.length);

//--- Limit to 5 Lines ---
logEM = logEM.slice(0,5);
scriptResults = logEM.join('\n');
scriptResults = scriptResults.replace(/^/gm,'• ');
//console.log("scriptResults: " + scriptResults);

}  //~~~~ END TRY ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

catch (oError) {

  //console.log(oError);

  if (oError.errorNumber === -128) {  // User Canceled

    scriptResults =  "[USER_CANCELED]\n\n"
      + "SCRIPT: " + ptyScriptName + "   Ver: " + ptyScriptVer
  }

  else {
    var errNum = oError.errorNumber || "Custom Error";
    scriptResults = "[ERROR]\n\n"
      + "MACRO:  " + "macroName" + "\n"
      + "SCRIPT: " + ptyScriptName + "   Ver: " + ptyScriptVer + "\n"
      + "Error Number: " + errNum + "\n"
      + oError.message

      var errMsg = oError.message;

      app.displayNotification(
        errMsg, 
        {
          withTitle: ptyScriptName,
          subtitle:  '[ERROR] Macro CANCELLED',
          soundName: "Glass"
        })

      throw new Error(scriptResults);


  } // END if/else on ERROR Number

} //~~~~ END TRY/CATCH BLOCK ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


//~~~~ END TRY/CATCH BLOCK ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

return scriptResults

})();

// EXAMPLE RESULTS:
// 2020-05-25 14:20:00 Execute macro “Process All New Evernote Notes in .InBox NB @EN” from trigger Every 5 minutes between 0:00-23:59 every day

property ptyScriptName : "Open Last N KM Log Lines in BBEdit"property ptyScriptVer : "1.0"property ptyScriptDate : "2020-05-24"property ptyScriptAuthor : "JMichaelTX"set kmInst to system attribute "KMINSTANCE"tell application "Keyboard Maestro Engine" to set numLogLines to getvariable "Local__LogLinesToOpen" instance kmInstif (numLogLines = "") then set numLogLines to "50"set cmdStr to "tail -r -n " & numLogLines & " ~/\"Library/Logs/Keyboard Maestro/Engine.log\""set logListStr to do shell script cmdStrtell application "BBEdit"	activate	set oDoc to make new text window with properties {contents:logListStr}
	tell oDoc to select insertion point before its text
end tell
