dict.string:
#!/bin/bash PATH=$PATH:/usr/local/bin
# Removes dot 2 to n, e.g., # 14.4.1 ==> 14.41
ver="$KMVAR_local_SystemVersion" echo "${ver%%.*}.$(echo ${ver#*.} | tr -d '.')"

"use strict";

// Rob Trew @2024
// Ver 0.02

ObjC.import("AppKit");

// main :: IO ()
// eslint-disable-next-line max-lines-per-function
const main = () => {
    const uw = ObjC.unwrap;

    return either(
        alert("XQuery report over KM Macros plist")
    )(
        xs => uw(xs).map(x => uw(x.stringValue))
.join("\n")
    )(
        bindLR(
            readPlistFileLR(kmPlistPath())
        )(
            fp => bindLR(
                plistFromDictLR(fp)
            )(
                xml => bindLR(
                    xmlDocFromStringLR(xml)
                )(
                    xQueryOverDocLR(kmvar.local_XQuery)
                )
            )
        )
    );
};

// ---------------- KEYBOARD MAESTRO -----------------

// kmPlistPath :: () -> IO FilePath
const kmPlistPath = () => {
    const
        kmMacros = [
            "/Keyboard Maestro/",
            "Keyboard Maestro Macros.plist"
        ].join("");

    return `${applicationSupportPath()}${kmMacros}`;
};

// ----------------------- JXA -----------------------

// alert :: String => String -> IO String
const alert = title =>
    s => {
        const sa = Object.assign(
            Application("System Events"), {
                includeStandardAdditions: true
            });

        return (
            sa.activate(),
            sa.displayDialog(s, {
                withTitle: title,
                buttons: ["OK"],
                defaultButton: "OK"
            }),
            s
        );
    };


// applicationSupportPath :: () -> String
const applicationSupportPath = () => {
    const uw = ObjC.unwrap;

    return uw(
        uw($.NSFileManager.defaultManager
        .URLsForDirectoryInDomains(
            $.NSApplicationSupportDirectory,
            $.NSUserDomainMask
        )
        )[0].path
    );
};


// doesFileExist :: FilePath -> IO Bool
const doesFileExist = fp => {
    const ref = Ref();

    return $.NSFileManager.defaultManager
    .fileExistsAtPathIsDirectory(
        $(fp)
        .stringByStandardizingPath, ref
    ) && 1 !== ref[0];
};


// filePath :: String -> FilePath
const filePath = s =>
    // The given file path with any tilde expanded
    // to the full user directory path.
    ObjC.unwrap(ObjC.wrap(s)
    .stringByStandardizingPath);


// plistFromDictLR :: JS Object -> Either String XML
const plistFromDictLR = jso => {
    const error = $();
    const xml = $.NSString.alloc.initWithDataEncoding(
        $.NSPropertyListSerialization
        .dataWithPropertyListFormatOptionsError(
            $(jso),
            $.NSPropertyListXMLFormat_v1_0, 0,
            error
        ),
        $.NSUTF8StringEncoding
    );

    return xml.isNil()
        ? Left(error.localizedDescription)
        : Right(
            ObjC.unwrap(xml)
        );
};


// readPlistFileLR :: FilePath -> Either String Dict
const readPlistFileLR = fp =>
    // Either a message or a dictionary of key-value
    // pairs read from the given file path.
    bindLR(
        doesFileExist(fp)
            ? Right(filePath(fp))
            : Left(`No file found at path:\n\t${fp}`)
    )(
        fpFull => {
            const
                e = $(),
                maybeDict = $.NSDictionary
                .dictionaryWithContentsOfURLError(
                    $.NSURL.fileURLWithPath(fpFull),
                    e
                );

            return maybeDict.isNil()
                ? (() => {
                    const
                        msg = ObjC.unwrap(
                            e.localizedDescription
                        );

                    return Left(`readPlistFileLR:\n\t${msg}`);
                })()
                : Right(ObjC.deepUnwrap(maybeDict));
        }
    );


// xmlDocFromStringLR ::
// XML String -> Either String NSXMLDocument
const xmlDocFromStringLR = xml => {
    const
        error = $(),
        xmlDoc = $.NSXMLDocument.alloc
        .initWithXMLStringOptionsError(
            xml, 0, error
        );

    return xmlDoc.isNil()
        ? Left(ObjC.unwrap(error.localizedDescription))
        : Right(xmlDoc);
};


// xQueryOverDocLR :: XQuery String ->
// XMLDoc -> [String]
const xQueryOverDocLR = xQuery =>
    // List of XQuery result strings for XQuery over doc.
    doc => {
        const
            uw = ObjC.unwrap,
            e = $(),
            xs = doc.objectsForXQueryError(xQuery, e);

        return xs.isNil()
            ? Left(uw(e.localizedDescription))
            : Right(xs);
    };


// --------------------- GENERIC ---------------------

// Left :: a -> Either a b
const Left = x => ({
    type: "Either",
    Left: x
});


// Right :: b -> Either a b
const Right = x => ({
    type: "Either",
    Right: x
});


// bindLR (>>=) :: Either a ->
// (a -> Either b) -> Either b
const bindLR = m =>
    mf => m.Left
        ? m
        : mf(m.Right);


// either :: (a -> c) -> (b -> c) -> Either a b -> c
const either = fl =>
    // Application of the function fl to the
    // contents of any Left value in e, or
    // the application of fr to its Right value.
    fr => e => e.Left
        ? fl(e.Left)
        : fr(e.Right);


// MAIN ---
return main();

#!/bin/bash PATH=$PATH:/usr/local/bin
threshold_seconds=$KMVAR_local_OldestTimeSeconds
tempfile=$(mktemp)
newer=0
while read line; do if [ $newer -eq 1 ]; then echo "$line" >> "$tempfile" ((log_cnt++))
else line_time_str=${line:0:19} line_time_seconds=$(date -j -f "%Y-%m-%d %H:%M:%S" "$line_time_str" "+%s")
if [ "$line_time_seconds" -ge "$threshold_seconds" ]; then newer=1 echo "$line" > "$tempfile" ((log_cnt++)) fi fi done < "$KMVAR_local_LogPath"
mv "$tempfile" "$KMVAR_local_LogPath"
echo $log_cnt

#!/bin/bash PATH=$PATH:/usr/local/bin
((days=$KMVAR_local_Days-1))
# local epoch for midnight local_Days days ago (i.e., local_Days=2 is yesterdays midnight date -j -v -${days}d -v 0H -v 0M -v 0S +%s

